import * as React from "react";
import {
  Button,
  Container,
  Input,
  Typography,
  TextareaAutosize,
  Grid,
} from "@mui/material";
import "./styles.css";
import { useState } from "react";
import Campo from "./Campo";

export default function App() {
  const [inputs, setInputs] = useState([]);
  const [id, setId] = useState(1);

  const handleAddInput = () => {
    setInputs([...inputs, <Campo key={id} />]);
    setId(id + 1);
  };

  const deleteAll = () => {
    setInputs([]);
    setId(0);
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={4}>
        <Item>
          <p>Agregar Ingrediente</p>
          <Button size="small" variant="outlined" onClick={handleAddInput}>
            Nuevo Ingrediente
          </Button>
          <Button size="small" variant="outlined" onClick={deleteAll}>
            Limpiar
          </Button>
          <p>Quitar Ingrediente</p>
          <div>{inputs}</div>
        </Item>
      </Grid>
      <Grid item xs={8}>
        <Item>
          <TextareaAutosize></TextareaAutosize>
        </Item>
      </Grid>
    </Grid>
  );
}
