import { Button, Input, Select, MenuItem } from "@mui/material";
import { useState } from "react";

export default function Campo({ id, onRemove }) {
  const [cantidad, setCantidad] = useState("Unidades");

  const handleChange = (event) => {
    setCantidad(event.target.value);
  };

  return (
    <div>
      <Input
        sx={{
          height: "2rem",
          width: "10rem",
          marginBottom: "1rem",
          border: 1,
          marginInline: "0.2rem",
          borderColor: "primary.main"
        }}
        placeholder={`Ingrediente ${id}`}
      />
      <Input
        type="number"
        sx={{
          height: "2rem",
          width: "60px",
          marginBottom: "1rem",
          border: 1,
          marginInline: "0.2rem",
          backgroundColor: "primary.light"
        }}
        placeholder="Cantidad"
      />
      <Select
        sx={{
          height: "2rem",
          width: "5rem",
          marginBottom: "1rem",
          border: 1,
          marginInline: "1rem",
          borderRadius: "25px",
          borderColor: "primary.main",
        }}
        labelId="demo-simple-select-label"
        id="cantidad"
        value={cantidad}
        label="Cantidad"
        onChange={handleChange}
      >
        <MenuItem value={"Unidades"}>Un</MenuItem>
        <MenuItem value={"Gramos"}>Gr</MenuItem>
        <MenuItem value={"Kilogramos"}>Kg</MenuItem>
      </Select>
      <Button size="small" variant="outlined" onClick={() => onRemove(id)}>
        Quitar
      </Button>
    </div>
  );
}
