import * as React from "react";
import {
  Button,
  Grid,
  Paper,
  Box,
  TextareaAutosize,
  Container,
} from "@mui/material";
import "./styles.css";
import { useState, useRef } from "react";
import Campo from "./Campo";
import ComponenteIA from "./ComponenteIA";

export default function App() {
  const [inputs, setInputs] = useState([]);
  const [id, setId] = useState(1);
  const [showCard, setShowCard] = useState(false);
  const [aiResponse, setAiResponse] = useState("");
  const [prompt, setPrompt] = useState("");
  const camposRefs = useRef([]);

  const handleAddInput = () => {
    setInputs([...inputs, { id }]);
    camposRefs.current.push(React.createRef());
    setId(id + 1);
  };

  const deleteAll = () => {
    setInputs([]);
    camposRefs.current = [];
    setId(1);
  };

  const handleRemoveCampo = (idToRemove) => {
    setInputs(
      inputs.filter((input, index) => {
        if (input.id !== idToRemove) return true;
        camposRefs.current.splice(index, 1);
        return false;
      })
    );
  };

  const changePaperState = () => {
    setShowCard(!showCard);
  };

  const handleBuscar = () => {
    const valores = camposRefs.current.map((ref) => ref.current.getValues());
    const filteredValores = JSON.stringify(
      valores.filter((valor) => valor.ingrediente && valor.cantidad)
    );
    const generatedPrompt = `Necesito que para esta lista ${filteredValores}, me digas alguna receta que pueda cocinar. No importa si es una respuesta larga, explayate lo que sea necesario, pero necesito que la respuesta sea solo el texto de la receta, ya que la voy a usar para mostrarla en mi pagina web.`;

    setPrompt(generatedPrompt);
    console.log(filteredValores);
  };

  const handlePeliculaObtenida = (response) => {
    setAiResponse(response);
    setShowCard(true); // Muestra el Paper cuando se obtiene la respuesta
  };

  const style = {
    background: "linear-gradient(to right, #ff7e5f, #feb47b)",
    height: "100vh",
    margin: 0,
  };

  return (
    <Grid
      container
      direction="column"
      justifyContent="center"
      alignItems="flex-start"
    >
      <Container maxWidth="sm">

      </Container>
      <Grid style={style} container spacing={2}>
        <Grid item xs={4}>
          <Box sx={{ marginBottom: "1rem" }}>
            <Button
              sx={{
                margin: "0.5rem",
                backgroundColor: "primary.main",
                color: "white",
                ":hover": { backgroundColor: "primary.dark" },
              }}
              size="small"
              variant="outlined"
              onClick={handleAddInput}
            >
              Nuevo Ingrediente
            </Button>
            <Button
              sx={{
                margin: "0.5rem",
                backgroundColor: "primary.main",
                color: "white",
                ":hover": { backgroundColor: "primary.dark" },
              }}
              size="small"
              variant="outlined"
              onClick={deleteAll}
            >
              Limpiar
            </Button>
          </Box>
          <div>
            {inputs.map((input, index) => (
              <Campo
                key={input.id}
                id={input.id}
                ref={camposRefs.current[index]}
                onRemove={handleRemoveCampo}
              />
            ))}
          </div>
          <Button
            sx={{
              margin: "0.5rem",
              backgroundColor: "primary.main",
              color: "white",
              ":hover": { backgroundColor: "primary.dark" },
            }}
            variant="outlined"
            onClick={handleBuscar}
          >
            Buscar
          </Button>
        </Grid>
        <Grid item xs={8}>
          <ComponenteIA
            prompt={prompt}
            onPeliculaObtenida={handlePeliculaObtenida}
          />
          <div style={{ padding: "20px" }}>
            {showCard && (
              <Paper
                elevation={5}
                style={{ padding: "20px", maxWidth: "400px", margin: "auto" }}
              >
                {aiResponse}
              </Paper>
            )}
          </div>
        </Grid>
      </Grid>
    </Grid>
  );
}
