import * as React from "react";
import {
  Button,
  Container,
  Input,
  Typography,
  TextareaAutosize,
} from "@mui/material";
import "./styles.css";
import { useState } from "react";
import Campo from "./Campo";

export default function App() {
  const [inputs, setInputs] = useState([]);
  const [id, setId] = useState(1);

  const handleAddInput = () => {
    setInputs([...inputs, <Campo key={id} />]);
    setId(id + 1);
  };

  const deleteAll = () => {
    setInputs([]);
    setId(0);
  };

  return (
    <Container>
      <Container>
        <p>Agregar Ingrediente</p>
        <Button size="small" variant="outlined" onClick={handleAddInput}>
          Nuevo Ingrediente
        </Button>
        <Button size="small" variant="outlined" onClick={deleteAll}>
          Limpiar
        </Button>
        <p>Quitar Ingrediente</p>
        <div>{inputs}</div>
      </Container>
      <Container></Container>
      <TextareaAutosize></TextareaAutosize>
    </Container>
  );
}
