import * as React from "react";
import {
  Button,
  Grid,
  Paper,
  Box,
  Typography,
  Container,
  CircularProgress
} from "@mui/material";
import { fetchData } from "./Gemini";
import "./styles.css";
import { useState, useRef } from "react";
import Campo from "./Campo";
import ReactMarkdown from 'react-markdown'
import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';

export default function App() {
  const [inputs, setInputs] = useState([]);
  const [id, setId] = useState(1);
  const [showCard, setShowCard] = useState(false);
  const [aiResponse, setAiResponse] = useState("");
  const camposRefs = useRef([]);
  const [loading, setLoading] = useState(false);

  const handleAddInput = () => {
    setInputs([...inputs, { id }]);
    camposRefs.current.push(React.createRef());
    setId(id + 1);
  };

  const deleteAll = () => {
    setInputs([]);
    camposRefs.current = [];
    setId(1);
  };

  const handleRemoveCampo = (idToRemove) => {
    setInputs(
      inputs.filter((input, index) => {
        if (input.id !== idToRemove) return true;
        camposRefs.current.splice(index, 1);
        return false;
      })
    );
  };

  const handleBuscar = async () => {
    setLoading(true);
    const valores = camposRefs.current.map((ref) => ref.current.getValues());
    const filteredValores = JSON.stringify(
      valores.filter((valor) => valor.ingrediente && valor.cantidad)
    );
    const generatedPrompt = `Necesito que para esta lista ${filteredValores}, me digas alguna receta que pueda cocinar. No importa si es una respuesta larga, explayate lo que sea necesario, pero necesito que la respuesta sea solo el texto de la receta, ya que la voy a usar para mostrarla en mi pagina web. Intenta darle un formato a la respuesta, no agregues caracteres especiales porque no quedan bien formateados.`;
    try {
      const response = await fetchData({ prompt: generatedPrompt });
      setLoading(false);
      setAiResponse(response);
      setShowCard(true); 
    } catch (error) {
      setLoading(false);
      console.error("Error al obtener los datos:", error);
      setAiResponse("Lo siento, hubo un error al obtener la receta. Por favor, intenta nuevamente.");
      setShowCard(true);
    }
  };

  const style = {
    background: "linear-gradient(to right, #ff7e5f, #feb47b)",
    minHeight: "100vh",
    padding: 0,
    margin: 0,
  };

  return (
    <Grid
      container
      direction="column"
      alignItems="flex-start"
      style={style}
    >
      <Container maxWidth="xl">
        <Typography variant="h2">
          Recetapp
        </Typography>
        <Typography variant="h6">
          Usa esta app para buscar una receta que te permita cocinar con lo que tengas a mano en tu casa.
          Solamente tenes que agregar los ingredientes que quieras y usando Gemini, la IA de Google, obtendras una receta, asi de sencillo.
        </Typography>
      </Container>
      <Grid container spacing={2}>
        <Grid item xs={12} md={4}>
          <Box sx={{ marginBottom: "1rem" }}>
            <Button
              sx={{
                margin: "0.5rem",
                backgroundColor: "primary.main",
                color: "white",
                ":hover": { backgroundColor: "primary.dark" },
              }}
              size="small"
              variant="outlined"
              onClick={handleAddInput}
            >
              Nuevo Ingrediente
            </Button>
            <Button
              sx={{
                margin: "0.5rem",
                backgroundColor: "primary.main",
                color: "white",
                ":hover": { backgroundColor: "primary.dark" },
              }}
              size="small"
              variant="outlined"
              onClick={deleteAll}
            >
              Limpiar
            </Button>
          </Box>
          <div>
            {inputs.map((input, index) => (
              <Campo
                key={input.id}
                id={input.id}
                ref={camposRefs.current[index]}
                onRemove={handleRemoveCampo}
              />
            ))}
          </div>
          <Button
            sx={{
              margin: "0.5rem",
              backgroundColor: "primary.main",
              color: "white",
              ":hover": { backgroundColor: "primary.dark" },
            }}
            variant="outlined"
            onClick={handleBuscar}
          >
            Buscar
          </Button>
        </Grid>
        <Grid item xs={12} md={8}>
          <div style={{ padding: "20px" }}>
            {showCard && (
              <Paper
                elevation={5}
                style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}
              >
                {loading ? (
                  <CircularProgress />
                ) : (
                  <ReactMarkdown

                  components={{
                    p: ({ node, ...props }) => <Typography variant="body1" {...props} sx={{ fontFamily: 'Roboto, sans-serif' }} />,
                    h1: ({ node, ...props }) => <Typography variant="h4" {...props} sx={{ fontFamily: 'Roboto, sans-serif' }} />,
                    h2: ({ node, ...props }) => <Typography variant="h5" {...props} sx={{ fontFamily: 'Roboto, sans-serif' }} />,
                    // Add more mappings as needed
                  }}
                  >
                    {aiResponse}
                  </ReactMarkdown>

                )}
              </Paper>
            )}
          </div>
        </Grid>
      </Grid>
    </Grid>
  );
}
