import { Button, Input, Select, MenuItem, InputLabel } from "@mui/material";
import { useState } from "react";

export default function Campo() {
  const [cantidad, setCantidad] = useState("Un");

  const handleChange = (event) => {
    setCantidad(event.target.value);
  };

  return (
    <>
      <Input
        sx={{
          width: "10rem",
          marginBottom: "1rem",
          border: 1,
          marginInline: "1rem",
        }}
      />
      <Input
        type="number"
        sx={{ width: "40px", marginBottom: "1rem", border: 1 }}
      />
      <Select
        sx={{
          height: "2rem",
          width: "10rem",
          marginBottom: "1rem",
          border: 1,
          marginInline: "1rem",
        }}
        labelId="demo-simple-select-label"
        id="cantidad"
        value={cantidad}
        label="Cantidad"
        onChange={handleChange}
      >
        <MenuItem value={"Unidades"}>Un</MenuItem>
        <MenuItem value={"Gramos"}>Gr</MenuItem>
        <MenuItem value={"Kilogramos"}>Kg</MenuItem>
      </Select>
      <Button size="small" variant="outlined">
        Quitar
      </Button>
    </>
  );
}
