import * as React from "react";
import { Button, TextareaAutosize, Grid } from "@mui/material";
import "./styles.css";
import { useState } from "react";
import Campo from "./Campo";

export default function App() {
  const [inputs, setInputs] = useState([]);
  const [id, setId] = useState(1);

  const handleAddInput = () => {
    setInputs([...inputs, {id}]);
    setId(id + 1);
    console.log(id);
  };

  const deleteAll = () => {
    setInputs([]);
    setId(1);
  };

  const handleRemoveCampo = (id) => {
    setInputs(inputs.filter(input => input.id !== id));
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={4}>
        <p>Agregar Ingrediente</p>
        <Button size="small" variant="outlined" onClick={handleAddInput}>
          Nuevo Ingrediente
        </Button>
        <Button size="small" variant="outlined" onClick={deleteAll} onRemove={handleRemoveCampo}>
          Limpiar
        </Button>
        <p>Quitar Ingrediente</p>
        <div>
          {inputs.map(input => (
            <Campo id={input.id} onRemove={handleRemoveCampo} />
          ))}
        </div>
      </Grid>
      <Grid item xs={8}>
        <TextareaAutosize></TextareaAutosize>
      </Grid>
    </Grid>
  );
}
