import * as React from "react";
import { Button, TextareaAutosize, Grid } from "@mui/material";
import "./styles.css";
import { useState } from "react";
import Campo from "./Campo";

export default function App() {
  const [inputs, setInputs] = useState([]);
  const [id, setId] = useState(1);

  const handleAddInput = () => {
    setInputs([...inputs, { id }]);
    setId(id + 1);
    console.log("Added input with id:", id);
    console.log("Current inputs:", inputs);
  };

  const deleteAll = () => {
    setInputs([]);
    setId(1);
    console.log("All inputs cleared");
  };

  const handleRemoveCampo = (idToRemove) => {
    setInputs(inputs.filter((input) => input.id !== idToRemove));
    console.log("Removed input with id:", idToRemove);
    console.log("Current inputs after removal:", inputs);
  };

  const style = {
    background: 'linear-gradient(to right, #ff7e5f, #feb47b)',
    height: '100vh',
    margin: 0,
};

  return (
    <Grid style={style }container spacing={2}>
      <Grid item xs={4}>
        <p>Agregar Ingrediente</p>
        <Button size="small" variant="outlined" onClick={handleAddInput}>
          Nuevo Ingrediente
        </Button>
        <Button size="small" variant="outlined" onClick={deleteAll}>
          Limpiar
        </Button>
        <div>
          {inputs.map((input) => (
            <Campo key={input.id} id={input.id} onRemove={handleRemoveCampo} />
          ))}
        </div>
        <Button  sx={{margin: "1rem",}} variant="outlined" onClick={deleteAll}>
          Buscar
        </Button>
      </Grid>
      <Grid item xs={8}>
        <TextareaAutosize></TextareaAutosize>
      </Grid>
    </Grid>
  );
}
