import * as React from "react";
import { Button, Grid, Paper, Box } from "@mui/material";
import "./styles.css";
import { useState, useRef } from "react";
import Campo from "./Campo";

export default function App() {
  const [inputs, setInputs] = useState([]);
  const [id, setId] = useState(1);
  const [showCard, setShowCard] = useState(false);
  const camposRefs = useRef([]);
  const [prompt,setPrompt] = useState("");

  const handleAddInput = () => {
    setInputs([...inputs, { id }]);
    camposRefs.current.push(React.createRef());
    setId(id + 1);
  };

  const deleteAll = () => {
    setInputs([]);
    camposRefs.current = [];
    setId(1);
  };

  const handleRemoveCampo = (idToRemove) => {
    setInputs(inputs.filter((input, index) => {
      if (input.id !== idToRemove) return true;
      camposRefs.current.splice(index, 1);
      return false;
    }));
  };

  const changePaperState = () => {
    setShowCard(!showCard);
  };

  const handleBuscar = () => {
    const valores = camposRefs.current.map(ref => ref.current.getValues());
    setPrompt  (JSON.stringify(valores));
  };

  const style = {
    background: "linear-gradient(to right, #ff7e5f, #feb47b)",
    height: "100vh",
    margin: 0,
  };

  return (
    <Grid style={style} container spacing={2}>
      <Grid item xs={4}>
        <Box sx={{ marginBottom: "1rem" }}>
          <p>Agregar Ingrediente</p>
          <Button sx={{margin: "0.5rem", backgroundColor:"primary.main" ,color:"white", ":hover":{backgroundColor:"primary.dark"}}} size="small" variant="outlined" onClick={handleAddInput}>
            Nuevo Ingrediente
          </Button>
          <Button sx={{margin: "0.5rem", backgroundColor:"primary.main" ,color:"white", ":hover":{backgroundColor:"primary.dark"}}} size="small" variant="outlined" onClick={deleteAll}>
            Limpiar
          </Button>
        </Box>
        <div>
          {inputs.map((input, index) => (
            <Campo
              key={input.id}
              id={input.id}
              ref={camposRefs.current[index]}
              onRemove={handleRemoveCampo}
            />
          ))}
        </div>
        <Button sx={{margin: "0.5rem", backgroundColor:"primary.main" ,color:"white", ":hover":{backgroundColor:"primary.dark"}}} variant="outlined" onClick={handleBuscar}>
          Buscar
        </Button>
      </Grid>
      <Grid item xs={8}>
        <div style={{ padding: '20px' }}>
          {prompt!=="" && (
            <Paper elevation={5} style={{ padding: '20px', maxWidth: '400px', margin: 'auto' }}>
              {prompt}
            </Paper>
          )}
        </div>
      </Grid>
    </Grid>
  );
}
