import { Button, Input, Select, MenuItem } from "@mui/material";
import { useState } from "react";

export default function Campo({ id, onRemove, onChange }) {
  const [state, setState] = useState({
    ingrediente: '',
    cantidad: '',
    unidad: 'Unidades'
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    const newState = { ...state, [name]: value };
    setState(newState);
    onChange(id, newState); // Notificar al componente padre del cambio
  };

  return (
    <div>
      <Input
        name="ingrediente"
        sx={{
          height: "2rem",
          width: "10rem",
          marginBottom: "1rem",
          border: 1,
          marginInline: "0.2rem",
          borderColor: "primary.main"
        }}
        placeholder="Agrega un Ingrediente"
        value={state.ingrediente}
        onChange={handleInputChange}
      />
      <Input
        name="cantidad"
        type="number"
        sx={{
          height: "2rem",
          width: "60px",
          marginBottom: "1rem",
          border: 1,
          marginInline: "0.2rem",
          borderColor: "primary.main"
        }}
        placeholder="Cantidad"
        value={state.cantidad}
        onChange={handleInputChange}
      />
      <Select
        name="unidad"
        sx={{
          height: "2rem",
          width: "5rem",
          marginBottom: "1rem",
          border: 1,
          marginInline: "1rem",
          borderRadius: "25px",
          borderColor: "primary.main"
        }}
        value={state.unidad}
        onChange={handleInputChange}
      >
        <MenuItem value={"Unidades"}>Un</MenuItem>
        <MenuItem value={"Gramos"}>Gr</MenuItem>
        <MenuItem value={"Kilogramos"}>Kg</MenuItem>
      </Select>
      <Button
        sx={{
          margin: "0.5rem",
          backgroundColor: "primary.main",
          color: "white",
          ":hover": { backgroundColor: "primary.dark" }
        }}
        size="small"
        variant="outlined"
        onClick={() => onRemove(id)}
      >
        Quitar
      </Button>
    </div>
  );
}
